import Foundationimport SwiftUIimport AVKit//struct per immettere videostruct VideoPlayerView: UIViewControllerRepresentable {    var videoName: String    var videoType: String    func makeUIViewController(context: Context) -> AVPlayerViewController {        if let videoPath = Bundle.main.path(forResource: videoName, ofType: videoType) {            let videoURL = URL(fileURLWithPath: videoPath)            let player = AVPlayer(url: videoURL)            let playerViewController = AVPlayerViewController()            playerViewController.player = player            return playerViewController        }        // Se il video non è presente, restituisci un AVPlayerViewController vuoto        return AVPlayerViewController()    }    func updateUIViewController(_ uiViewController: AVPlayerViewController, context: Context) {        // Aggiornamenti, se necessario    }}//sezione esercizi Total bodystruct TB1: View{ //il piu' facile    var body: some View{        VStack{            Text("Circuit 1")                .font(.largeTitle)            VideoPlayerView(videoName: "VideoTB1", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n1. Alternately lower the arms while lowering the center of gravity of your body in an upright position, as in the normal practice of a squat.\n2. Rotate the arms and straighten the back to settle the body into its natural upright position.\n\nDone this exercise 2 times for 8 repetitions.")                .position(x:195,y:50)        }    }}#Preview{    TB1()} struct TB2: View{ //il piu' difficile    var body: some View{        VStack{            Text("Circuit 2")                .font(.largeTitle)            VideoPlayerView(videoName: "VideoTB2", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n\n\n\n\n\n1. Position yourself face down with your arms bent and your feet resting on the ground.\n2. Move one hand to the center of the mat, rotating in the opposite direction and placing the other hand on the ear.\n3. Place one leg on the ground and one extended.\n4. Bend the extended leg towards the elbow and return to the initial position.\n5. Draw the hip towards parallel with the other, lifting it and lowering it in line with the mat.\n6. Extend the leg upward again, then bend it and perform a push-up.\n7. Return to the starting position, performing the exercise on the other side and slightlying separate the knees.\n9. Point our feet on the mat.\n10. Apply a downward-facing dog style pose.\n11. Perform running, bending the legs one at a time to loosen the muscles.\n\nDone this exercise 4 times for 8 repetitions.")                .position(x:195,y:50)        }    }}#Preview{    TB2()}struct TB3: View{ //piu' difficile che facile    var body: some View{        VStack{            Text("Circuit 3")                .font(.largeTitle)            VideoPlayerView(videoName: "VideoTB3", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n\n\n\n\n\n1. Lie on your back with your knees bent and feet flat on the ground, hip-width apart.\n2. Cross your hands behind your head, with elbows open to the sides.\n3. Exhale as you slowly lift your head and neck off the mat, bringing your chin towards your chest and keeping the lower back pressed into the floor.\n4. Inhale as you extend your legs along the floor, keeping your feet lifted about 45 degrees from the ground.\n5. Exhale as you lift your torso off the mat, bringing your chest towards your knees and extending your arms forward parallel to the floor.\n6. Hold the position for a deep breath, then slowly begin to return to the starting position, exhaling as you lower your torso and head, and inhaling as you bend your knees and bring your feet back to the ground.\n7. Extend your body, with feet parallel to the mat and arms by your sides.\n8. Inhale and, as you lift the lower body, bring your arms overhead, possibly resting them on the mat.\n9. Exhale and bring the arms back down to the starting position.\n\nDone this exercise 3 times for 10 repetitions.")                .position(x:195,y:50)        }    }}#Preview{    TB3()}struct TB4: View{ //piu' facile che difficile    var body: some View{        VStack{            Text("Circuit 4")                .font(.largeTitle)            VideoPlayerView(videoName: "VideoTB4", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n\n\n\n\n\n1. Extend your body, with feet parallel to the mat but not too close to the glutes, and hands on the nape to support the head.\n2. Lift one leg, while keeping it still, extend the other leg in a line at a 45-degree angle in front of you, and bring the head to the chest.\n3. Fold both legs and pull them towards the chest, releasing the position of our lumbar area.\n4. Extend your body, with feet parallel to the mat but not too close to the glutes, and hands on the nape to support the head.\n5. Lift one leg, while keeping it still, extend the other leg in an elongated position and bring the head to the chest.\n6. Fold both legs and pull them towards the chest, releasing the position of our lumbar area.\n\nDone this exercise 3 times for 8 repetitions.")                .position(x:195,y:50)        }    }}#Preview{    TB4()}//sezione esercizi pilatesstruct pilates1: View{    var body: some View{        VStack{            Text("Side-to-Side Hip Shift")                .font(.largeTitle)            VideoPlayerView(videoName: "pilates1", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n\n\n\n\n1. Stand up straight with your feet slightly apart.\n2. Place your hands on your hips or let them hang down by your sides.\n3. Slowly shift your hips from right to left.\n4. Focus on keeping your torso upright and your weight evenly distributed on your feet.\n5. Repeat the movement back and forth for the desired number of times or for a certain period of time.\n\nRepeat this exercise for 30 seconds.")                .position(x:195,y:50)        }    }}#Preview{    pilates1()}struct pilates2: View{    var body: some View{        VStack{            Text("Alternative Hip Circles")                .font(.largeTitle)            VideoPlayerView(videoName: "pilates2", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n1. Stand with your hands on your hips.\n2. Move your hips forward and backward, keeping the movement limited to this direction.\n3. Continue for the desired number of repetitions or for a certain period of time.\n\nRepeat this exercise for 30 seconds.")                .position(x:195,y:50)        }    }}#Preview{    pilates2()}struct pilates3: View{    var body: some View{        VStack{            Text("Hip Circles")                .font(.largeTitle)            VideoPlayerView(videoName: "pilates3", videoType: "mov")                .frame(height: 300)                .frame(width: 350, height: 250)                .cornerRadius(20)            Text("\n\n\n\n\n1. Stand with your hands on your hips.\n2. Make small circles with your hips, moving them forward, backward, and laterally.\n3. Keep the movement fluid and controlled.\n4. You can perform the movement in both directions.\n5. Repeat for the desired number of times.\n\nRepeat this exercise for 30 seconds.")                .position(x:195,y:50)        }    }}#Preview{    pilates3()}struct pilates4: View{    var 